mapper.select:
/* homenet.shop.brand.service.BrandService.selectBrandList 브랜드 정보 조회*/ SELECT Z.* FROM ( SELECT ROWNUM RNUM , X.* , COUNT(0) OVER () AS TOTAL_COUNT FROM ( SELECT A.BRND_NO , A.BRND_NM , A.BRND_KOR_NM , A.BRND_ENG_NM , A.BRND_DESC_CONT
FROM TB_BRND_BASE A
) X )Z
WHERE Z.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

/* homenet.shop.brand.service.BrandService.selectBrandImgList 브랜드 이미지 정보 조회*/ SELECT Z.* FROM ( SELECT ROWNUM RNUM , X.* , COUNT(0) OVER () AS TOTAL_COUNT FROM ( SELECT A.IMG_NO , A.BRND_NO , A.IMG_NM , A.IMG_PATH_NM , A.IMG_FILE_NM
FROM TB_BRND_IMG_INFO A WHERE A.BRND_NO = #{brndNo } ) X )Z
WHERE Z.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.insert:
/* homenet.shop.brand.service.BrandService.insertBrand, 브랜드 정보 등록 */
INSERT INTO TB_BRND_BASE (
	  BRND_NO
	, BRND_NM
	, BRND_KOR_NM
	, BRND_ENG_NM
	, BRND_DESC_CONT
	
) VALUES (
	  #{brndNo 			}
	, #{brndNm 			}
	, #{brndKorNm 		}
	, #{brndEngNm 		}
	, #{brndDescCont 	}
	
)

mapper.update:
/* homenet.shop.brand.service.BrandService.updateBrandBase, 브랜드 정보 수정 */
UPDATE TB_BRND_BASE SET
	  BRND_NO				= #{brndNo 			}
	, BRND_NM               = #{brndNm 			}
	, BRND_KOR_NM           = #{brndKorNm 		}
	, BRND_ENG_NM           = #{brndEngNm 		}
	, BRND_DESC_CONT        = #{brndDescCont 	}
	
WHERE BRND_NO = #{brndNo	}

mapper.delete:
/* homenet.shop.brand.service.BrandService.deleteBrandBase, 브랜드 정보 삭제 */
DELETE FROM TB_BRND_BASE
WHERE BRND_NO = #{brndNo	}

mapper.insert:
/* homenet.shop.brand.service.BrandService.insertBrandImgInfo, 브랜드 이미지 정보 등록 */

    SELECT  SQ_BRND_IMG_INFO_01.NEXTVAL AS imgNo FROM DUAL

INSERT INTO TB_BRND_IMG_INFO (
	  IMG_NO
	, BRND_NO
	, IMG_NM
	, IMG_PATH_NM
	, IMG_FILE_NM
	
) VALUES (
	  #{imgNo 			}
	, #{brndNo 			}
	, #{imgNm 			}
	, #{imgPathNm 		}
	, #{imgFileNm 		}
	
)

mapper.update:
/* homenet.shop.brand.service.BrandService.updateBrandImgInfo, 브랜드 이미지 정보 수정 */
UPDATE TB_BRND_IMG_INFO SET
	  IMG_NO			= #{imgNo 			}
	, BRND_NO       	= #{brndNo 			}
	, IMG_NM        	= #{imgNm 			}
	, IMG_PATH_NM       = #{imgPathNm 		}
	, IMG_FILE_NM       = #{imgFileNm 		}
	
WHERE IMG_NO = #{imgNo	}

mapper.delete:
/* homenet.shop.brand.service.BrandService.deleteBrandImgInfo, 브랜드 이미지 정보 삭제 */
DELETE FROM TB_BRND_IMG_INFO
WHERE BRND_NO = #{brndNo	}
