mapper.select:
/* homenet.shop.display.service.DisplayService.selectDisplayList 전시매장 정보 조회*/ SELECT Z.* FROM ( SELECT ROWNUM RNUM , X.* , COUNT(0) OVER () AS TOTAL_COUNT FROM ( SELECT A.DISP_NO , A.UPR_DISP_NO , A.DPML_NO , A.DISP_NM , A.DISP_GNB_NM , A.GNB_EXP_METH_CD , A.DISP_TIT_NM , A.DISP_TIT_EXP_METH_CD , A.SHOP_TP_CD , A.SHOP_DESC_CONT , A.DPTH_NO , A.DISP_PRIO_RNK , A.USE_YN , A.DISP_YN , A.TLWT_LF_YN , A.PRT_TP_CD , A.LST_SORT_CD , A.MOV_FRME_CD , A.LNK_URL_ADDR , A.LNK_SPDP_HH_NO , A.DISP_LRG_NO , A.DISP_MID_NO , A.DISP_SML_NO , A.DISP_THN_NO , A.MENU_USE_YN , A.TMPL_NO , A.PPP_SN
FROM TB_DISP_SHOP_BASE A
) X )Z
WHERE Z.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.insert:
/* homenet.shop.display.service.DisplayService.insertDisplayBase, 전시매장 정보 등록 */
INSERT INTO TB_DISP_SHOP_BASE (
	  DISP_NO
	, UPR_DISP_NO
	, DPML_NO
	, DISP_NM
	, DISP_GNB_NM
	, GNB_EXP_METH_CD
	, DISP_TIT_NM
	, DISP_TIT_EXP_METH_CD
	, SHOP_TP_CD
	, SHOP_DESC_CONT
	, DPTH_NO
	, DISP_PRIO_RNK
	, USE_YN
	, DISP_YN
	, TLWT_LF_YN
	, PRT_TP_CD
	, LST_SORT_CD
	, MOV_FRME_CD
	, LNK_URL_ADDR
	, LNK_SPDP_HH_NO
	, DISP_LRG_NO
	, DISP_MID_NO
	, DISP_SML_NO
	, DISP_THN_NO
	, MENU_USE_YN
	, TMPL_NO
	, PPP_SN
	
) VALUES (
	  #{dispNo				}
	, #{uprDispNo       	}
	, #{dpmlNo          	}
	, #{dispNm          	}
	, #{dispGnbNm       	}
	, #{gnbExpMethCd    	}
	, #{dispTitNm       	}
	, #{dispTitExpMethCd	}
	, #{shopTpCd        	}
	, #{shopDescCont    	}
	, #{dpthNo          	}
	, #{dispPrioRnk     	}
	, #{useYn           	}
	, #{dispYn          	}
	, #{tlwtLfYn        	}
	, #{prtTpCd         	}
	, #{lstSortCd       	}
	, #{movFrmeCd       	}
	, #{lnkUrlAddr      	}
	, #{lnkSpdpHhNo     	}
	, #{dispLrgNo       	}
	, #{dispMidNo       	}
	, #{dispSmlNo       	}
	, #{dispThnNo       	}
	, #{menuUseYn       	}
	, #{tmplNo          	}
	, #{pppSn           	}
	
)

mapper.update:
/* homenet.shop.display.service.DisplayService.updateDisplayBase, 전시매장 정보 수정 */
UPDATE TB_DISP_SHOP_BASE SET
	  DISP_NM					= #{dispNm          	}
	, DISP_GNB_NM               = #{dispGnbNm       	}
	, GNB_EXP_METH_CD           = #{gnbExpMethCd    	}
	, DISP_TIT_NM               = #{dispTitNm       	}
	, DISP_TIT_EXP_METH_CD      = #{dispTitExpMethCd	}
	, SHOP_TP_CD                = #{shopTpCd        	}
	, SHOP_DESC_CONT            = #{shopDescCont    	}
	, DPTH_NO                   = #{dpthNo          	}
	, DISP_PRIO_RNK             = #{dispPrioRnk     	}
	, USE_YN                    = #{useYn           	}
	, DISP_YN                   = #{dispYn          	}
	, TLWT_LF_YN                = #{tlwtLfYn        	}
	, PRT_TP_CD                 = #{prtTpCd         	}
	, LST_SORT_CD               = #{lstSortCd       	}
	, MOV_FRME_CD               = #{movFrmeCd       	}
	, LNK_URL_ADDR              = #{lnkUrlAddr      	}
	, LNK_SPDP_HH_NO            = #{lnkSpdpHhNo     	}
	, DISP_LRG_NO               = #{dispLrgNo       	}
	, DISP_MID_NO               = #{dispMidNo       	}
	, DISP_SML_NO               = #{dispSmlNo       	}
	, DISP_THN_NO               = #{dispThnNo       	}
	, MENU_USE_YN               = #{menuUseYn       	}
	, TMPL_NO                   = #{tmplNo          	}
	, PPP_SN                    = #{pppSn           	}
WHERE DISP_NO = #{dispNo				}

mapper.delete:
/* homenet.shop.display.service.DisplayService.deleteDisplayBase, 전시매장 정보 수정 */
DELETE FROM TB_DISP_SHOP_BASE
WHERE DISP_NO = #{dispNo				}

mapper.insert:
		/* homenet.shop.display.service.DisplayService.insertDisplayImgInfo, 전시 이미지 정보 등록 */
		
            SELECT 
            	NVL(MAX(IMG_SN),0)+1
            FROM TB_DISP_IMG_INFO
            WHERE DISP_NO 		= #{dispNo}
            AND DISP_IMG_TP_CD 	= #{displayImageTypeCode}
        
    	
    
        INSERT INTO TB_DISP_IMG_INFO (
              DISP_NO
            , DISP_IMG_TP_CD
            , IMG_SN
            , DISP_SHOP_SCT_CD
            , BNR_IMG_PATH_NM
            , BNR_IMG_FILE_NM
            , LNK_URL_ADDR
         		
        ) VALUES (
              
              #{dispNo       	}
            , #{dispImgTpCd  	}
            , #{imgSn        	}
            , #{dispShopSctCd	}
            , #{bnrImgPathNm 	}
            , #{bnrImgFileNm 	}
            , #{lnkUrlAddr   	}
            
        )

mapper.delete:
/* homenet.shop.display.service.DisplayService.deleteDisplayImgInfo, 전시 이미지 정보 수정 */
DELETE FROM TB_DISP_IMG_INFO
WHERE DISP_NO = #{dispNo				}
