mapper.select:
/* homenet.shop.article.service.GoodsArticleService.selectGoodsArtcCdList 품목군 정보 조회*/ SELECT Z.* , (SELECT ARTC_NM FROM TB_GOODS_ARTC_CD WHERE ARTC_CD = SUBSTR(Z.ARTC_CD,0,2) ) AS ARTC_UP_NM
, LEVEL
FROM ( SELECT ROWNUM RNUM , A.* , COUNT(0) OVER () AS TOTAL_COUNT FROM ( SELECT A.ARTC_CD , SUBSTR(A.ARTC_CD,0,2) AS ARTC_UP_CD , A.EC_ARTC_CD , A.UPR_ARTC_CD , A.ARTC_DPTH_NO , A.ARTC_NM , A.ARTC_FULL_NM , A.ONL_BRCH_INV_RT , A.MAX_LMT_QTY , A.GOODS_MRGN_RT , A.SIZE_LKTB_FILE_NM , A.SIZE_LKTB_PATH_NM , FN_COMM_NM('EC_ARTC_CD', EC_ARTC_CD) AS EC_ARTC_NM , (SELECT ARTC_NM FROM TB_GOODS_ARTC_CD WHERE ARTC_CD = A.UPR_ARTC_CD) AS UPR_ARTC_NM
FROM TB_GOODS_ARTC_CD A ) A
) Z
START WITH Z.UPR_ARTC_CD IS NULL CONNECT BY PRIOR Z.ARTC_CD = Z.UPR_ARTC_CD ORDER SIBLINGS BY Z.ARTC_CD ASC
WHERE Z.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

/* homenet.shop.article.service.GoodsArticleService.selectGoodsArtcCdTreeList 품목군 Tree 정보 조회*/ SELECT A.* FROM ( SELECT A.ARTC_CD , A.EC_ARTC_CD , A.UPR_ARTC_CD , A.ARTC_DPTH_NO , A.ARTC_NM , A.ARTC_FULL_NM , A.ONL_BRCH_INV_RT , A.MAX_LMT_QTY , A.GOODS_MRGN_RT , A.SIZE_LKTB_FILE_NM , A.SIZE_LKTB_PATH_NM , FN_COMM_NM('EC_ARTC_CD', EC_ARTC_CD) AS EC_ARTC_NM , (SELECT COUNT(0) AS CHILD_COUNT FROM TB_GOODS_ARTC_CD WHERE UPR_ARTC_CD = A.ARTC_CD) AS CHILD_COUNT
FROM TB_GOODS_ARTC_CD A
AND A.UPR_ARTC_CD = #{artcCd }
AND A.UPR_ARTC_CD IS NULL AND A.ARTC_DPTH_NO = 1
) A ORDER BY A.ARTC_CD ASC

mapper.insert:
/* homenet.shop.article.service.GoodsArticleService.insertGoodsArtcCd, 품목군 정보 등록 */
INSERT INTO TB_GOODS_ARTC_CD (
	  ARTC_CD
	, EC_ARTC_CD
	, UPR_ARTC_CD
	, ARTC_DPTH_NO
	, ARTC_NM
	, ARTC_FULL_NM
	, ONL_BRCH_INV_RT
	, MAX_LMT_QTY
	, GOODS_MRGN_RT
	, SIZE_LKTB_FILE_NM
	, SIZE_LKTB_PATH_NM
	
) VALUES (
	  #{artcCd				}
	, #{ecArtcCd 			}
	, #{uprArtcCd 	  		}
	, #{artcDpthNo 	  		}
	, #{artcNm    		 	}
	, #{artcFullNm    		}
	, #{onlBrchInvRt		}
	, #{maxLmtQty			}
	, #{goodsMrgnRt			}
	, #{sizeLktbFileNm 		}
	, #{sizeLktbPathNm 		}
	
)

mapper.update:
/* homenet.shop.article.service.GoodsArticleService.updateGoodsArtcCd, 품목군 정보 수정 */
UPDATE TB_GOODS_ARTC_CD SET
	  ARTC_CD				= #{artcCd				}
	, EC_ARTC_CD            = #{ecArtcCd 			}
	, UPR_ARTC_CD           = #{uprArtcCd 	  		}
	, ARTC_DPTH_NO          = #{artcDpthNo 	  		}
	, ARTC_NM               = #{artcNm    		 	}
	, ARTC_FULL_NM          = #{artcFullNm    		}
	, ONL_BRCH_INV_RT       = #{onlBrchInvRt		}
	, MAX_LMT_QTY           = #{maxLmtQty			}
	, GOODS_MRGN_RT         = #{goodsMrgnRt			}
	, SIZE_LKTB_FILE_NM     = #{sizeLktbFileNm 		}
	, SIZE_LKTB_PATH_NM     = #{sizeLktbPathNm 		}
	
WHERE ARTC_CD = #{artcCd	}

mapper.delete:
/* homenet.shop.article.service.GoodsArticleService.deleteGoodsArtcCd, 품목군 정보 삭제 */
DELETE FROM TB_GOODS_ARTC_CD
WHERE ARTC_CD = #{artcCd	}
