mapper.select:
/* homenet.shop.login.service.LoginService.selectLogin_S02 */ SELECT nw101tl.user_no AS userNo , nw101tl.login_id AS loginId , nw101tl.pwd_fail_tms AS pwdFailTms , CASE WHEN TO_CHAR(SYSDATE-90, 'yyyyMMddHH24miss') > nw101tl.pwd_chng_dttm THEN 'N' ELSE 'Y' END AS pwdChgYn , CASE WHEN TO_CHAR(SYSDATE, 'yyyyMMddHH24miss') > nw101tl.optr_expy_dttm THEN 'Y' ELSE 'N' END AS optrExpireYn , nw101tl.optr_auth_cd AS optrAuthCd , wrk002.WRK002_KOR_NM AS userNm , TO_CHAR(SYSDATE, 'yyyyMMddHH24miss') AS loginTime FROM NWRK101TL nw101tl , NWRK100TL nw100tl , WRK002VW wrk002 WHERE nw101tl.login_id = #{loginId} AND nw101tl.mbr_dc = 'K'
AND nw101tl.optr_auth_cd = '01'
AND wrk002.wrk002_join_dept = 'Y' AND nw101tl.login_id = wrk002.wrk002_one_id AND nw101tl.user_no = nw100tl.user_no AND nw101tl.scde_yn = 'N' AND wrk002.wrk002_encode_pw = CRYPTMD5(#{loginPwd})

mapper.insert:
        /* homenet.shop.login.service.LoginService.insertLogin_I01 */
		INSERT INTO NWRK151TL (
		    user_no, cnnt_log_no, mbl_yn
		  , cnnt_y, cnnt_m, cnnt_d, cnnt_tmhh, cnnt_dttm
		  , cnip, cnnt_info, os_cd
		) VALUES (
		    #{userNo}, SQ_NWRK151TL1.NEXTVAL, #{mblYn}
		  , TO_CHAR(sysdate, 'yyyy'), TO_CHAR(sysdate, 'MM'), TO_CHAR(sysdate, 'dd'), TO_CHAR(sysdate, 'HH24'), TO_CHAR(sysdate, 'yyyyMMddHH24miss')
		  , #{remoteAddr}, #{cnntInfo}, #{osCd}
		)

mapper.select:
        /* homenet.shop.login.service.LoginService.selectLogin_S05 */
		SELECT COUNT(user_no)
		    FROM NWRK101TL
		   WHERE login_id = #{loginId}
		     AND MBR_DC = 'K'
		     AND SCDE_YN = 'N'

mapper.insert:
        /* homenet.shop.login.service.LoginService.insertLogin_I02 */
		INSERT INTO nwrk100tl (
		        user_no, user_nm
		      , rps_stno
		      , wrt_pn_no, wrt_pn_ip, wrt_dttm
		      , updt_pn_no, updt_pn_ip, updt_dttm
		) VALUES (
		        #{userNo}, #{userNm}
		      , #{rpsStno}
		      , #{userNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'yyyyMMddHH24miss')
		      , #{userNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'yyyyMMddHH24miss')
		)

mapper.update:
        /* homenet.shop.login.service.LoginService.updateLogin_U01 */
		UPDATE NWRK100TL
		SET user_nm = #{userNm}
		  , rps_stno = #{rpsStno}
		  , updt_pn_no = (SELECT user_no FROM NWRK101TL WHERE login_id = #{loginId} AND MBR_DC = 'K' AND SCDE_YN = 'N') 
		  , updt_pn_ip = #{remoteAddr}
		  , updt_dttm = TO_CHAR(SYSDATE, 'yyyyMMddHH24miss')
		WHERE user_no = (SELECT user_no FROM NWRK101TL WHERE login_id = #{loginId} AND MBR_DC = 'K' AND SCDE_YN = 'N')

mapper.insert:
        /* homenet.shop.login.service.LoginService.insertLogin_I04 */
		INSERT INTO NWRK150TL (
		    user_no, cnnt_log_no, mbl_yn
		  , cnnt_y, cnnt_m, cnnt_d, cnnt_tmhh, cnnt_dttm
		  , cnip, cnnt_info, os_cd
		) VALUES (
		    #{userNo}, SQ_NWRK150TL1.NEXTVAL, #{mblYn}
		  , TO_CHAR(sysdate, 'yyyy'), TO_CHAR(sysdate, 'MM'), TO_CHAR(sysdate, 'dd'), TO_CHAR(sysdate, 'HH24'), TO_CHAR(sysdate, 'yyyyMMddHH24miss')
		  , #{remoteAddr}, #{cnntInfo}, #{osCd}
		)

mapper.update:
        /* homenet.shop.login.service.LoginService.updateLogin_U01 */
		UPDATE NWRK101TL
		    SET pwd_fail_tms = NVL(pwd_fail_tms, 0)+1
		WHERE login_id = #{loginId}
		  AND mbr_dc = #{loginDiv}

        /* homenet.shop.login.service.LoginService.updateLogin_U01 */
		UPDATE NWRK101TL
		    SET pwd_fail_tms = 0
		WHERE user_no = #{userNo}
