mapper.select:
/** getCodeGroupSubList   **/
     SELECT 
        GRP_CD_ID, CD_ID, CD_NM, SORT_OR, USE_YN
    FROM NWRK200TL
    WHERE GRP_CD_ID = #{grpCdId}
    
      AND HGRK_GRP_CD_ID = #{hgrkGrpCdId}
    
    
      AND HGRK_CD_ID = #{hgrkCdId}
    
    
      AND REFE_V_1 = #{refeV1}
    
    
      AND REFE_V_2 = #{refeV2}
    
    
      AND REFE_V_3 = #{refeV3}
    
    
      AND REFE_V_4 = #{refeV4}
    
    
      AND REFE_V_5 = #{refeV5}
    
      AND USE_YN = 'Y'
    ORDER BY SORT_OR ASC

SELECT Y.GRP_CD_ID, Y.CD_ID, Y.CD_NM, Y.SORT_OR, Y.USE_YN FROM ( SELECT X.GRP_CD_ID, X.CD_ID, X.CD_NM, X.SORT_OR, X.USE_YN, ROWNUM RO FROM ( SELECT GRP_CD_ID, CD_ID, CD_NM, SORT_OR, USE_YN FROM NWRK200TL WHERE 1 = 1
AND GRP_CD_ID = #{searchGrpCdId}
AND CD_NM LIKE '%' || #{searchWord} || '%'
ORDER BY GRP_CD_ID, SORT_OR ) X ) Y WHERE RO BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT COUNT(*) FROM NWRK200TL WHERE 1 = 1
AND GRP_CD_ID = #{searchGrpCdId}
AND CD_NM LIKE '%' || #{searchWord} || '%'

mapper.insert:

INSERT INTO NWRK200TL (
      GRP_CD_ID
    , CD_ID
    , CD_NM
    , CD_CN 
    , SORT_OR
    , USE_YN
    , CNNCT_INS_CD
    , CNNCT_INS_CD_NM
    , REFE_V_1
    , REFE_V_2
    , REFE_V_3
    , REFE_V_4
    , REFE_V_5
    , WRT_PN_NO
    , WRT_DTTM
    , UPDT_PN_NO
    , UPDT_DTTM
) VALUES ( 
      #{grpCdId} 
    , #{cdId}
    , #{cdNm}
    , #{cdCn} 
    , #{sortOr}
    , #{useYn}
    , #{cnnctInsCd}
    , #{cnnctInsCdNm}
    , #{refeV1}
    , #{refeV2}
    , #{refeV3}
    , #{refeV4}
    , #{refeV5}
    , #{wrtPnNo}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{updtPnNo}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:

UPDATE NWRK200TL SET 
      GRP_CD_ID             = #{grpCdId}
    , CD_ID                 = #{cdId}
    , CD_NM                 = #{cdNm}
    , CD_CN                 = #{cdCn}
    , SORT_OR               = #{sortOr}
    , USE_YN                = #{useYn}
    , CNNCT_INS_CD          = #{cnnctInsCd}
    , CNNCT_INS_CD_NM       = #{cnnctInsCdNm}
    , REFE_V_1              = #{refeV1}
    , REFE_V_2              = #{refeV2}
    , REFE_V_3              = #{refeV3}
    , REFE_V_4              = #{refeV4}
    , REFE_V_5              = #{refeV5}
    , UPDT_PN_NO            = #{updtPnNo}
    , UPDT_DTTM             = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE GRP_CD_ID = #{grpCdId}
    AND CD_ID   = #{cdId}

mapper.delete:

DELETE FROM NWRK200TL
WHERE GRP_CD_ID = #{grpCdId}
    AND CD_ID   = #{cdId}
