mapper.select:
/* site.menu.service.MenuService.list */ SELECT * FROM ( SELECT LEVEL , Z.SORT_OR , Z.MENU_NO , Z.HGRK_MENU_NO , Z.OPTR_PGM_NO , Z.MENU_NM , Z.USE_YN , Z.OPTR_PGM_NM FROM (SELECT A.SORT_OR , A.MENU_NO , A.HGRK_MENU_NO , A.OPTR_PGM_NO , A.MENU_NM , A.USE_YN , (SELECT X.OPTR_PGM_NM FROM TB_OPTR_PGM_INFO X WHERE X.OPTR_PGM_NO = A.OPTR_PGM_NO) AS OPTR_PGM_NM FROM TB_OPTR_MENU_INFO A
AND A.USE_YN = #{useYn}
) Z START WITH Z.HGRK_MENU_NO = 0 CONNECT BY PRIOR Z.MENU_NO = Z.HGRK_MENU_NO ORDER SIBLINGS BY Z.SORT_OR )

mapper.insert:
/* site.menu.service.MenuService.createMenu */
SELECT SQ_OPTR_MENU.NEXTVAL FROM DUAL
/* createMenu*/ INSERT INTO TB_OPTR_MENU_INFO (MENU_NO, HGRK_MENU_NO, MENU_NM, OPTR_PGM_NO, SORT_OR, USE_YN, WRT_PN_NO, WRT_PN_IP, WRT_DTTM, UPDT_PN_NO, UPDT_PN_IP, UPDT_DTTM) SELECT #{seqMenuNo}, #{hgrkMenuNo}, #{menuNm}, #{optrPgmNo}, NVL((SELECT MAX(SORT_OR) + 1 FROM TB_OPTR_MENU_INFO WHERE HGRK_MENU_NO = #{hgrkMenuNo}), 1), #{useYn}, #{loginUserNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), #{loginUserNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') FROM TB_OPTR_MENU_INFO WHERE MENU_NO = #{hgrkMenuNo}

/* site.menu.service.MenuService.createMenu2 */
SELECT SQ_OPTR_MENU.NEXTVAL FROM DUAL
/* createMenu2*/ INSERT INTO TB_OPTR_MENU_INFO (MENU_NO, HGRK_MENU_NO, MENU_NM, OPTR_PGM_NO, SORT_OR, USE_YN, WRT_PN_NO, WRT_PN_IP, WRT_DTTM, UPDT_PN_NO, UPDT_PN_IP, UPDT_DTTM) VALUES (#{seqMenuNo}, #{hgrkMenuNo}, #{menuNm}, #{optrPgmNo}, NVL((SELECT MAX(SORT_OR) + 1 FROM TB_OPTR_MENU_INFO WHERE HGRK_MENU_NO = #{hgrkMenuNo}), 1), #{useYn}, #{loginUserNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), #{loginUserNo}, #{remoteAddr}, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') )
