/* site.program.service.ProgramService.selectProgramList */ SELECT A.* FROM (SELECT Z.*, ROWNUM RO FROM (SELECT OPTR_PGM_NO, OPTR_PGM_NM, OPTR_PGM_ID, OPTR_PGM_URL_V, WRT_DTTM, OPTR_PGM_GRP_NO, MENU_USE_YN FROM TB_OPTR_PGM_INFO
AND DECODE(#{searchKey},'2',OPTR_PGM_ID,'3',OPTR_PGM_URL_V, OPTR_PGM_NM) LIKE '%'||#{searchWord}||'%'
AND OPTR_PGM_NO NOT IN (SELECT OPTR_PGM_NO FROM TB_OPTR_PGM_AUTH WHERE OPTR_AUTH_NO = TO_NUMBER(#{searchOptrAuthNo}))
AND MENU_USE_YN = #{searchMenuUseYn}
AND OPTR_PGM_GRP_NO LIKE #{searchOptrPgmGrpNo}||'%'
ORDER BY OPTR_PGM_GRP_NO, OPTR_PGM_ID, OPTR_PGM_NM ) Z ) A WHERE RO BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

/* site.program.service.ProgramService.selectProgramListTotCnt */ SELECT COUNT(*) FROM TB_OPTR_PGM_INFO
AND DECODE(#{searchKey},'2',OPTR_PGM_ID,'3',OPTR_PGM_URL_V, OPTR_PGM_NM) LIKE '%'||#{searchWord}||'%'
AND OPTR_PGM_NO NOT IN (SELECT OPTR_PGM_NO FROM TB_OPTR_PGM_AUTH WHERE OPTR_AUTH_NO = TO_NUMBER(#{searchOptrAuthNo}))
AND MENU_USE_YN = #{searchMenuUseYn}
AND OPTR_PGM_GRP_NO LIKE #{searchOptrPgmGrpNo}||'%'
